function extractImdbId(n){const t=n.match(/tt\d{7,}/);return t?t[0]:null}function redirectToMovie(){const n=document.getElementById("imdbInput").value.trim(),t=document.getElementById("errorMsg");if(t.textContent="",!n)return void(t.textContent="Please enter IMDb URL or ID.");const e=extractImdbId(n);if(!e)return void(t.textContent="Invalid IMDb input. Correct format: tt24060892");window.location.href=`https://imdbs.pages.dev/?id=${e}`}